const burger=document.querySelector(".burger"),nav=document.querySelector(".header__nav"),links=document.querySelectorAll(".nav__link");burger.onclick=()=>{for(nav.classList.toggle("header__nav--active"),i=0;i<links.length;++i)links[i].classList.toggle("nav__link--active"),links[i].style.transition=`.${i}s`},nav.addEventListener("mouseup",(()=>{for(nav.classList.remove("header__nav--active"),i=0;i<links.length;++i)links[i].classList.remove("nav__link--active")}));