const{form:form}=document.forms,ifCheckOrRadio=e=>["checkbox","radio"].includes(e);function sendFormValue(e){e.preventDefault();const o={};for(let e of form){const{name:n}=e;if(n){const{type:c,checked:r,value:t}=e;o[n]=ifCheckOrRadio(c)?r:t}}console.log(o)}form.addEventListener("submit",sendFormValue);